%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  0 -  Proposta de trabalho - parte 2%%%%  a. Cada aluno determina as frequencias fo, f1 e f2 (3x) - cada vogal 9 valores - 45 valores.%%  %%  A1	fo = 170,8 /// f1 = 697,5 /// f2 = 1225,1%%  A2	fo = 144,7 /// f1 = 718,6 /// f2 = 1305%%  A3	fo = 166,9 /// f1 = 669,1	/// f2 = 1341,5%%  E1	fo = 184,6 /// f1 = 370,5 /// f2 = 1997,3%%  E2	fo = 185,4 /// f1 = 365,6 /// f2 = 1972,9%%  E3	fo = 180,4 /// f1 = 361,3 /// f2 = 1991,3%%  I1	fo = 201,5 /// f1 = 2042,9/// f2 = 2657,4%%  I2	fo = 188,1 /// f1 = 1906,2/// f2 = 3062,3%%  I3	fo = 188,1 /// f1 = 2103,3/// f2 = 2847,9%%  O1	fo = 164,1 /// f1 = 664,7 /// f2 = 823,6%%  O2	fo = 169,8 /// f1 = 624,1 /// f2 = 944,1%%  O3	fo = 172,7 /// f1 = 647,5 /// f2 = 988,3%%  U1	fo = 194,8 /// f1 = 695,3 /// f2 = 3163,7%%  U2	fo = 208,6 /// f1 = 636,6 /// f2 = 3210,1%%  U3	fo = 188,7 /// f1 = 562,3 /// f2 = 3315,8%%%%%%%%  b. Base de conhecimento Numero de alunos x 45.%% %%  DISPONIVEL ANEXO JUNTO AO TRABALHO: t2.xlsx%%  A PLANILHA UTILIZADA FOI COM BASE NOS DADOS DE ATE 14/04%%%%%%  RESOLUCAO AO LONGO DO CODIGO%%%%  c. Histrograma de cada vogal - media e o desvio padrao de cada frequencia para cada vogal.%%%%  RESOLUCAO AO LONGO DO CODIGO%%%%  d. Elimnar valores fora de contexto e verificar diferencas da base%%%%  RESOLUCAO AO LONGO DO CODIGO%%%%  e. Gravar cinco vogais que nao serao usadas na base. Serao usadas somente para teste.%%%%  UTILIZAR OS ARQUIVOS WAV ABASE,EBASE,IBASE,OBASE,UBASE PARA TESTE%%%%  f. 1 - acertou e 0 - errou --> a,e,i,o,u --> 1 1 0 0 1%%%%  RESOLUCAO NO ARQUIVO PYTHON, ESTE ARQUIVO ESTA NA PASTA ItemF%%%%  g. Qualidade do sistema de aprendizado para cada vogal%%%%  RESOLUCAO AO LONGO DO CODIGO%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparacao do codigo %% %% Boas praticas: limpeza de variaveis; variaveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;          % limpa visual da tela de comandosclose all;    % limpa as figurasclear all;    % limpa as variaveis%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Problema%%%% %% sinal g(k) - sinal amostrado%%% sinal de referencia - artificial%%% com senoides%%% sinal a ser trabalhado - gk%% ALTERAR O NOME DO ARQUIVO .WAV PARA O QUE DEVERA SER ANALISADO%% (A1,A2,A3,E1,E2,E3,I1,I2,I3,O1,O2,O3,U1,U2,U3)[gk,fs] = audioread('U3.wav');          % leitura de um sinal real - vogal.%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  3 -  Pre- processamento do sinal%% %%  Ambiente de cadalculo integral e simbolico%%  %%  X[n] = 1/N somatoria_{k=0}^{N-1} g(k) exp(-j*n*k*2*pi/N)%%%%N          = length(gk);               % numero de pontos de g(k)Ts         = 1/fs;                     % Taxa de amostragemDuracao    = N*Ts;                     % duracao do sinal g(k)ws         = 2*pi*fs;                  % frequencia angulartempo      = linspace(0,Duracao,N);    % vetor tempo computacional fmax       = fs/2;                     % frequencia maximafrequencia = linspace(-fmax,fmax,N);   % frequencias de interesse                %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 4 -  Calculo de X(n)%%  %%  X[n] = 1/N somatoria_{k=0}^{N-1} g(k) exp(-j*n*k*2*pi/N)%%%%%%% segunda estrutura: utilizando algebra lineartic;                                % inicia um contator de tempoMatriz_J      = (exp(-j*2*pi/N))*ones(N,N);   % cria a matriz de coeficientesMatriz_nk     = [0:1:N-1]'*[0:1:N-1];         % matriz dos expoentes nkWN            = Matriz_J.^Matriz_nk;          % matriz de Fourier - constanteXn            = WN*gk;                       % Serie discreeta de Fouriertoc;                                % determina o tempo decorrido%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  6 -  Visualizacao%%%%figure(2)plot(tempo,gk,'k-','linewidth',3)     % configura plot(x,y, cor azul e linha cheia)xlabel('Tempo em segundos')           % tempo em segundosylabel('Amplitude')                   % amplitude em voltstitle('Sinal g(k) amostrado')         % titulogridfigure(3)%%% fftshift rotaciona o vetor 0 --> 2*pi ; -pi --> +piplot(frequencia,fftshift(log10(abs(Xn))),'k-','linewidth',3)     % configura plot(x,y, cor azul e linha cheia)xlabel('Frequencia em Hz')                      % tempo em segundosylabel('Amplitude')                             % amplitude em voltstitle('Espectro de amplitude')                  % titulogrid%% b. Base de conhecimento Numero de alunos x 45.%%  %% %%%%pkg load io;              %% Utilizando o leitor de xls do package io%%%% Criacao de vetores para guardar os dados de f0,f1 e f2 de cada vogal%%%% Para a vogal AAf0 = xlsread ('t2.xlsx', 'Vogal a', 'A2:A139');Af1 = xlsread ('t2.xlsx', 'Vogal a', 'B2:B139'); Af2 = xlsread ('t2.xlsx', 'Vogal a', 'C2:C139'); %% Para a vogal EEf0 = xlsread ('t2.xlsx', 'Vogal e', 'A2:A139');Ef1 = xlsread ('t2.xlsx', 'Vogal e', 'B2:B139'); Ef2 = xlsread ('t2.xlsx', 'Vogal e', 'C2:C139'); %% Para a vogal IIf0 = xlsread ('t2.xlsx', 'Vogal i', 'A2:A139');If1 = xlsread ('t2.xlsx', 'Vogal i', 'B2:B139'); If2 = xlsread ('t2.xlsx', 'Vogal i', 'C2:C139');%% Para a vogal OOf0 = xlsread ('t2.xlsx', 'Vogal o', 'A2:A139');Of1 = xlsread ('t2.xlsx', 'Vogal o', 'B2:B139'); Of2 = xlsread ('t2.xlsx', 'Vogal o', 'C2:C139');         %% Para a vogal UUf0 = xlsread ('t2.xlsx', 'Vogal u', 'A2:A139');Uf1 = xlsread ('t2.xlsx', 'Vogal u', 'B2:B139'); Uf2 = xlsread ('t2.xlsx', 'Vogal u', 'C2:C139'); %% c. Histrograma de cada vogal - media e o desvio padrao de cada frequencia para cada vogal.%%pkg load statistics;      %% Carrega pacote estatistico%%%%VOGAL A%%%%Af0%%Histogramafigure(4)hist(Af0)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Af0')                        % titulogrid%%Boxplotfigure(5)boxplot(Af0)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Af0')                             % titulogrid%%Media e desvio padraoMAf0 = mean(Af0); %%169,42DAf0 = std(Af0);  %%53,425%%Af1%%Histogramafigure(6)hist(Af1)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Af1')                        % titulogrid%%Boxplotfigure(7)boxplot(Af1)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Af1')                             % titulogrid%%Media e desvio padraoMAf1 = mean(Af1); %%655,36DAf1 = std(Af1);  %%181,51%%Af2%%Histogramafigure(8)hist(Af2)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Af2')                        % titulogrid%%Boxplotfigure(9)boxplot(Af2)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Af2')                             % titulogrid%%Media e desvio padraoMAf2 = mean(Af2); %%1197,5DAf2 = std(Af2);  %%397,12%%VOGAL E%%%%Ef0%%Histogramafigure(10)hist(Ef0)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Ef0')                        % titulogrid%%Boxplotfigure(11)boxplot(Ef0)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Ef0')                             % titulogrid%%Media e desvio padraoMEf0 = mean(Ef0); %%176,05DEf0 = std(Ef0);  %%75,08%%Ef1%%Histogramafigure(12)hist(Ef1)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Ef1')                        % titulogrid%%Boxplotfigure(13)boxplot(Ef1)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Ef1')                             % titulogrid%%Media e desvio padraoMEf1 = mean(Ef1); %%532,69DEf1 = std(Ef1);  %%268,19%%Ef2%%Histogramafigure(14)hist(Ef2)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma Ef2')                        % titulogrid%%Boxplotfigure(15)boxplot(Ef2)ylabel('Frequencia')                             % Frequenciatitle('Boxplot Ef2')                             % titulogrid%%Media e desvio padraoMEf2 = mean(Ef2); %%1889,5DEf2 = std(Ef2);  %%401,57%%VOGAL I%%%%If0%%Histogramafigure(16)hist(If0)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma If0')                        % titulogrid%%Boxplotfigure(17)boxplot(If0)ylabel('Frequencia')                             % Frequenciatitle('Boxplot If0')                             % titulogrid%%Media e desvio padraoMIf0 = mean(If0); %%217,74DIf0 = std(If0);  %%67,406%%If1%%Histogramafigure(18)hist(If1)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma If1')                        % titulogrid%%Boxplotfigure(19)boxplot(If1)ylabel('Frequencia')                             % Frequenciatitle('Boxplot If1')                             % titulogrid%%Media e desvio padraoMIf1 = mean(If1); %%1767,8DIf1 = std(If1);  %%675,14%%If2%%Histogramafigure(20)hist(If2)xlabel('Faixa de Frequencia')                  % faixa de frequenciaylabel('Aparicoes')                            % quantidade de aparicoestitle('Histograma If2')                        % titulogrid%%Boxplo